## Component Testing Pattern

```typescript
// src/components/shop/FlavorRoster.test.tsx
import { render, screen, fireEvent } from '@testing-library/react';
import { FlavorRoster } from './FlavorRoster';
import { mockGameState, mockWorkers } from '@/test-utils/testState';

describe('FlavorRoster', () => {
  it('renders all workers when present', () => {
    const state = mockGameState();
    const workers = Object.values(mockWorkers);

    render(<FlavorRoster workers={workers} featuredFlavor="strawberry" />);

    expect(screen.getByText('Vanilla')).toBeInTheDocument();
    expect(screen.getByText('Strawberry')).toBeInTheDocument();
