  });
});
```

## Engine Testing Pattern

```typescript
// src/engine/encounterEngine.test.ts
import { describe, it, expect } from 'vitest';
import { resolveEncounter, createEncounter } from './encounterEngine';
import { mockGameState } from '@/test-utils/testState';

describe('encounterEngine', () => {
  it('correctly identifies incorrect documentation', () => {
    const state = mockGameState({
      hidden: { ...mockGameState().hidden, documentationQuality: 'sloppy' },
    });

    const encounter = createEncounter('cotton-candy', state);

    expect(encounter.documentation.quality).toBe('incorrect');
  });

  it('removes worker when player complies with correct paperwork', () => {
    const state = mockGameState();
    const encounter = createEncounter('pistachio', state);
    encounter.documentation.quality = 'correct';

    const result = resolveEncounter(encounter, { choice: 'comply' }, state);

    expect(result.stateChanges.workersRemoved).toContain('pistachio');
  });
});
```

## Content Testing Pattern

```typescript
// src/content/generators/encounterGenerator.test.ts
import { describe, it, expect } from 'vitest';
import { generateEncounterDialogue } from './encounterGenerator';

describe('encounterGenerator', () => {
  it('generates more polite dialogue at higher pressure', () => {
    const low = generateEncounterDialogue('declaration', 'calm', 'vanilla', 'sloppy');
    const high = generateEncounterDialogue('declaration', 'critical', 'vanilla', 'correct');

    // Higher pressure should have MORE politeness markers
    expect(countPolitenessMarkers(high.text)).toBeGreaterThan(countPolitenessMarkers(low.text));
  });

  it('never generates threatening language', () => {
    const pressures: HeatLevel[] = ['calm', 'watched', 'tense', 'critical'];

    pressures.forEach(pressure => {
      const dialogue = generateEncounterDialogue('declaration', pressure, 'strawberry', 'correct');
      expect(dialogue.text).not.toMatch(/threat|force|will|must|demand/i);
    });
  });
});
```

